рдпрд╣рд╛рдБ, рдПрдХ pop рд╡рд┐рдзрд┐ рд▓рд┐рдВрдХ рдЧрд░рд┐рдПрдХреЛ рд╕реВрдЪреАрдмрд╛рдЯ рдорд╛рди рд╣рдЯрд╛рдЙрди рд╕рд┐рд░реНрдЬрдирд╛ рдЧрд░рд┐рдПрдХреЛ рдЫред
class Node {
    constructor(data) {
        this.data = data
        this.next = null 
    }
}

class LinkedList {
    constructor(head) {
        this.head = head 
    }
    pop = () => {
        let current = this.head 
        while (current.next.next) {
            current = current.next 
        }
        current.next = current.next.next 
    }
}

